import Axios from "axios";

const url = "https://covid19.mathdro.id/api";

export const fetchData = async () => {
  const {
    data: { confirmed, recovered, deaths, lastUpdate }
  } = await Axios.get(url);
  return {
    confirmed,
    recovered,
    deaths,
    lastUpdate,
  };
};

export const fetchDailyData = async () => {
  const {
    data
  } = await Axios.get(`${url}`)
}
