import Axios from "axios";

const url = "https://covid19.mathdro.id/api";

export const fetchData = async () => {
  const {
    data: { confirmed, recovered, deaths, lastUpdate },
  } = await Axios.get(url);
  const modifiedData = {
    confirmed,
    recovered,
    deaths,
    lastUpdate,
  };
  return modifiedData;
};
